/**
 * nylonに登録する関数のクラス
 */
class nylonfunc {

	/**
	 * コンストラクタ
	 * @param { function } func - コールバック関数
	 * @param { nylon } object - nylonオブジェクト
	 */
	constructor( func, object ) {
		/** @type { function } */
		this.fn = func;
		/** @type { nylon } */
		this.obj = object;
	}
}
